    public static void setUpQuestions(Scanner keyBoard, String[][][] questions, Map<String, Integer> map) {
        for (int i = 0; i < 9; i++) {
            System.out.println(questions[i][0][0]);
            System.out.println(questions[i][1][0]);
            System.out.println(questions[i][2][0]);
            System.out.println(questions[i][3][0]);
            System.out.println(questions[i][4][0]);
            System.out.print("Your answer: ");
            int userInput = keyBoard.nextInt();
            System.out.println();
            String key = questions[i][userInput][1];
            map.put(key, (Integer) map.get(key) + 1);
        }

        System.out.println(questions[9][0][0]);
        System.out.println(questions[9][1][0]);
        System.out.println(questions[9][2][0]);
        System.out.println(questions[9][3][0]);
        System.out.println(questions[9][4][0]);
        System.out.println(questions[9][5][0]);
        System.out.println(questions[9][6][0]);
        System.out.print("Your answer: ");
        int choice = keyBoard.nextInt();
        System.out.println();
        String keyTwo = questions[9][choice][1];
        map.put(keyTwo, (Integer) map.get(keyTwo) + 1);


        int max = 0;
        String philosopher = "";
        for (String name: map.keySet()) {
            int count = map.get(name);
            if (count > max) {
                max = count;
                philosopher = name;
            }
        }
        System.out.println();
        System.out.print("Your best match is: " + philosopher + ", ");
        double similarity = ((double) max / 7) * 100;
        System.out.println("with a similarity of " + similarity + "%.");
        System.out.println();
        if (philosopher.equals("Camus")) {
            camus();
        } else if (philosopher.equals("Kierkegaard")) {
            kierkegaard();
        } else if (philosopher.equals("Heidegger")) {
            heidegger();
        } else if (philosopher.equals("Sartre")) {
            sartre();
        } else if (philosopher.equals("Beauvoir")) {
            beauvoir();
        } else if (philosopher.equals("Nietzsche")) {
            nietzsche();
        }

        System.out.println("\n");
    }



